<?php class MyException {     public function __construct($message, $code = 0, Exception $previous = null) {       echo $message;       } }  class TestException {     public $var;     function __construct($avalue) {          switch ($avalue) {             case 1:                 throw new MyException('1 is an invalid parameter', 5);                 break;             default:                  $this->var = $avalue;                 break;         }     } }  try {     $o = new TestException(1); }  catch (Exception $e) {           echo "Default Exception\n"; } catch (MyException $e) {         echo "My exception\n";     }  ?>